INSERT INTO events(eventname, email, eventdate, eventtime, eventlocation, latitude, longitude, eventdescription) VALUES ('Downtown Santa Cruz: Holiday Parade', 'santa@ucsc.com', '2022-12-10', '2:30:00', 'Locust Street, Santa Cruz, CA, USA', 36.9744503, -122.0307556, 'The traditional holiday parade is the official kickoff for the winter holidays celebration. One never knows exactly what to expect at the Parade, but you can be sure to expect the whole community to show up and have a great time - and of course, at the end of the parade, sitting in an antique fire truck, the man of the hour is welcomed back to Downtown Santa Cruz. Bring the family or participate! The Holiday Parade is Rain or Shine!');
