import logo from './img/LocalendarLogo.svg';
import React, { useCallback } from 'react';
import './App.css';
import { useEffect, useState } from 'react';
import jwt_decode from "jwt-decode";
import { Navigate } from "react-router-dom";
from 

function App() {
  // state not designed to be used cross components
  const [ user, setUser ] = useState({});
  const [ redirectHome, setRedirectHome ] = useState(false)

  function handleSignIn(response) {
    var user_object = jwt_decode(response.credential);
    setUser(user_object);
    console.log(user_object);
    document.getElementById("sign-in-div").hidden = true;
    window.profile_name = user_object.name;
    setRedirectHome(true);
  }
  function handleSignOut(event) {
    setUser({});
    setRedirectHome(false)
  }

  useEffect(() => {
    /* global google */
    setUser({});
    google.accounts.id.initialize({
      client_id: "792919383337-pnkkhet0sprdmlf00ojncglrujh3ve0r.apps.googleusercontent.com",
      callback: handleSignIn
    });

    google.accounts.id.renderButton(
      document.getElementById("sign-in-div"),
      { theme: "outline", size: "large" }
    )
  }, []);

  return (
    <Router>
      <div className='home'>
        <div className='logoDiv'>
          <img src={logo} alt='logo' className='logoClass'/>
          <div className='loginBox'>
          <div>
            <div id="sign-in-div"></div>
            {redirectHome && <Navigate to="/homepage" replace={true}/>}
          </div>
        </div>
        </div>
      </div>
      <Switch>
        <Route path="/homepage">
          <Home userInfo={user}/>
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
