import React from 'react';
import logo from './img/LocalendarLogo.svg';
import Home from './Home'
import './App.css';
import {useNavigate} from 'react-router-dom';
import { useEffect, useState } from 'react';

/**
 * Simple component with no state.
 *
 * @param {function} setDummy set the dummy state
 */
function getDummy(setDummy) {
  fetch('http://localhost:3010/v0/dummy')
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      setDummy(json.message);
    })
    .catch((error) => {
      setDummy(`ERROR: ${error.toString()}`);
    });
}


class LoginBox extends React.Component {
  render() {
    return (
      <p>Login stuff goes here!!!</p>
    );
  }
}

/**
 * Simple component with one state variable.
 *
 * @return {object} JSX
 */
function Dummy() {
  const [dummy, setDummy] = React.useState('Click the button!');
  const [homeDisplay, setHomeDisplay] = React.useState('none');
  const [loginDisplay, setLoginDisplay] = React.useState('block');
  const history = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log({name: 'shruti', email: 'shruti@ucsc.edu'});
    fetch('http://localhost:3010/v0/dummy', {
      method: 'POST',
      body: JSON.stringify({name: 'shruti', email: 'shruti@ucsc.edu'}),
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((res) => {
        // console.log(res);
        if (!res.ok) {
          throw res;
        } return res.json();
      })
      .then((json) => {
        console.log('correct password');
        console.log(json);
        localStorage.setItem('user', JSON.stringify(json));
        history('/dummy');
      })
      .catch((err) => {
        console.log(err);
        alert('Error logging in, please try again!!!!');
      });
    // const data = new FormData(event.currentTarget);
    /**
    console.log({
      email: data.get('email'),
      password: data.get('password'),
    });
    */
  };

  return (
    <div>
      <div>
        <div style={{display: homeDisplay}}>
          <Home/>
        </div>
        <div className='home' style={{display: loginDisplay}}>
          <div className='logoDiv'>
            <img src={logo} alt='logo' className='logoClass'/>
            <div className='loginBox'>
              <div>
                <LoginBox/>
                <div onClick={()=>{setHomeDisplay('block'); setLoginDisplay('none')}}>Click me for home page!</div>
                <h3> Click button to connect to the Backend dummy endpoint</h3>
                  <button
                    aria-label='get dummy'
                    onClick={handleSubmit}
                    >
                    Get Dummy
                   </button>
                  <p/>
                <h3 aria-label='dummy message'>{dummy}</h3>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>

  );
}



export default Dummy;
